mapper.select:
  	select (@rowNO := @rowNo+1) AS trans_id,roll_in_account,trans_amount,create_time,`describe` from (
  
	    select trans_id,roll_in_account,trans_amount,create_time,`describe`
		from points_transation
		where roll_out_account = #{rollOutAccount,jdbcType=VARCHAR}
		   
		      and roll_in_account = #{rollInAccount,jdbcType=VARCHAR}
		   
		   
		      and create_time >= date_format(#{updateUser,jdbcType=VARCHAR},'%Y-%m-%d')
		   
		   
		      and create_time <= date_format(#{createUser,jdbcType=VARCHAR},'%Y-%m-%d')
		   
	   
  	)x,(select @rowNO :=0) z
